"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CAdapiDataSrc = require('syracuse-x3/lib/fusion/CAdapiDataSrc').CAdapiDataSrc;
var CAdapiDataSrcLine = require('syracuse-x3/lib/fusion/CAdapiDataSrcLine').CAdapiDataSrcLine;
var CAdapiVariableFactory = require('syracuse-x3/lib/fusion/CAdapiVariableFactory').CAdapiVariableFactory;

var CAdapiListOfDataSrcsFactory = exports.CAdapiListOfDataSrcsFactory = {

	newEmptyDataSrc: function(aStackedWindow, aScreen, aEntityIndexBaseOne, aId, aDataSourceNode) {

		var wDim = aDataSourceNode.$.DIM;
		var wNbField = aDataSourceNode.$.NBZ;
		var wBasPagName = aDataSourceNode.$.NBP;
		var dataSrc = new CAdapiDataSrc(aStackedWindow, aScreen, aEntityIndexBaseOne, aId, wDim, wNbField, wBasPagName);
		var srcLine = this.newEmptyDataSrcLine(aStackedWindow, dataSrc, aId, aDataSourceNode);
		if (dataSrc.isBlocTableau()) {
			var wLineEntrySup = srcLine.clone();
			dataSrc.setEntrySup(wLineEntrySup);
			/*
			 * GLUTE: gestion des formats dans les colonnes des tableaux
			 *
			 * pour associer chacune des variables de la ligne a sa
			 * correspondante dans la ligne "wLineEntrySup" Note: sert ala
			 * propagation du format courant a chaque modification de valeur
			 */
			srcLine.setFormaterEntity(wLineEntrySup);
		}

		dataSrc.appendFirstBlankLine(srcLine);
		return dataSrc;
	},
	newEmptyDataSrcLine: function(aStackedWindow, aDataSrc, aId, aDataSourceNode) {
		var wNbField = aDataSourceNode.$.NBZ;
		var wAdapiDataSrcLine = new CAdapiDataSrcLine(aStackedWindow, aDataSrc, aId, wNbField);
		var wListOfX3Variables = aDataSourceNode.FLD;
		var wMax = Array.isArray(wListOfX3Variables) ? wListOfX3Variables.length : 1;
		if (wListOfX3Variables && wMax > 0) {
			var wVariable;
			var wVariableNode;
			var wI = 0;
			while (wI < wMax) {
				wVariableNode = wMax === 1 ? wListOfX3Variables : wListOfX3Variables[wI];
				wVariable = CAdapiVariableFactory.instanciateVariable(aStackedWindow, wAdapiDataSrcLine, wI + 1, aId, wVariableNode, 0);
				wAdapiDataSrcLine.addOneVar(wVariable);
				wI++;
			}
		}
		return wAdapiDataSrcLine;
	},
	newScreenDataSrcs: function(aStackedWindow, aScreenNode, aScreen, aDataSrcs) {
		var wDataSourcesNodes = aScreenNode.DS;
		var wMax = Array.isArray(wDataSourcesNodes) ? wDataSourcesNodes.length : 1;
		var wDataSourceNode;
		var wAdapiDataSrc;
		var wDataSourceID;
		var wI = 0;
		while (wI < wMax) {
			wDataSourceNode = wMax === 1 ? wDataSourcesNodes : wDataSourcesNodes[wI];
			wDataSourceID = wDataSourceNode.$.NAM;
			wAdapiDataSrc = this.newEmptyDataSrc(aStackedWindow, aScreen, wI + 1, wDataSourceID, wDataSourceNode);
			aScreen.pushDataSrc(wDataSourceID, wAdapiDataSrc);
			aDataSrcs.pushEntity(wDataSourceID, wAdapiDataSrc);
			wI++;
		}
	}
};