"use strict";
var helpers = require('syracuse-core/lib/helpers');
var util = require('util');

var CAdapiListOfEntity = exports.CAdapiListOfEntity = helpers.defineClass(function(aAdapiRequesterWindow) {
	Array.call(this);
	this.adapiRequesterWindow = aAdapiRequesterWindow;
	this.listedThings = {};
}, Array, {
	get: function(aId) {
		//console.log("GET listedThing element="+util.inspect(this.listedThings));
		//console.log("Test: "+Object.keys(this.listedThings)[0]);
		return this.listedThings[Object.keys(this.listedThings)[aId]];
	},
	getAdapiClient: function() {
		return this.adapiRequesterWindow.adapiClient;
	},
	pushEntity: function(aId, aAdapiEntity) {
		this[this.length] = aAdapiEntity;
		this.listedThings[aId] = aAdapiEntity;
	},
	size: function() {
		return Object.keys(this.listedThings).length;
	},
});