"use strict";
var helpers = require('syracuse-core/lib/helpers');
var ArrayList = Array; //require('syracuse-x3/lib/fusion/ArrayList').ArrayList;
var Hashtable = Object;

var CAdapiListOfEntity = exports.CAdapiListOfEntity = helpers.defineClass(function(aAdapiRequesterWindow) {
	ArrayList.call(this);
	this.adapiRequesterWindow = aAdapiRequesterWindow;
	this.listedThings = new Hashtable();
}, ArrayList, {
	get: function(aId) {
		return this.listedThings.get(aId);
	},
	getAdapiClient: function() {
		return this.adapiRequesterWindow.adapiClient;
	},
	push: function(aId, aAdapiEntity) {
		this.add(aAdapiEntity);
		this.listedThings.put(aId, aAdapiEntity);
	}
});