"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CAdapiVariable = require('syracuse-x3/lib/fusion/CAdapiVariable').CAdapiVariable;

var CAdapiVariableAlpha = exports.CAdapiVariableAlpha = helpers.defineClass(function(srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState) {
	CAdapiVariable.call(this, srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState);
}, CAdapiVariable, {
	clone: function() {
		var wVariable = new CAdapiVariableAlpha(this.srcLine, this.entityIndexBaseOne, this.name, this.id, this.dataType, this.format, this.state);
		wVariable.setMustBeFormated(this.mustBeFormated());
		this.cloneValues(wVariable);
		return wVariable;
	},
	unFormatData: function(aValue) {
		if (!this.mustBeFormated()) {
			return aValue;
		}
		if (aValue == null) {
			return null;
		}
		if (aValue.length == 0) {
			return aValue;
		}
		return this.callUnFormater(aValue);
	}
});