"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CAdapiVariable = require('syracuse-x3/lib/fusion/CAdapiVariable').CAdapiVariable;

var CAdapiVariablePass = exports.CAdapiVariablePass = helpers.defineClass(function(srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState) {
	CAdapiVariable.call(this, srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState);
}, CAdapiVariable, {
	acceptEditFormat: function() {
		return false;
	},
	clone: function() {
		var wVariable = new CAdapiVariablePass(this.srcLine, this.entityIndexBaseOne, this.name, this.id, this.dataType, this.format, this.state);
		wVariable.mustBeFormated = this.mustBeFormated;
		this.cloneValues(wVariable);
		return wVariable;
	},
	setInitialValue: function(aValue) {
		aValue.setStruValue(CCrypton.X3Decrypt(aValue.value));
		CAdapiVariable.prototype.storeBrowserValue.call(this,aValue);
	}
});