"use strict";
var helpers = require('syracuse-core/lib/helpers');

var WIN_MODELE_DIVERSE = 1;
var WIN_TYPE_CLIENTE = 1;
var PREFIX_SPECIAL = '_';

var CAdapiWindow = exports.CAdapiWindow = helpers.defineClass(function(aModele, aType, aNbLignes, aNbColonnes) {
	this.modele = aModele;
	this.type = aType;
	this.nbLignes = aNbLignes;
	this.nbColonnes = aNbColonnes;
}, null, {
	setNbColonnes: function(aNbColonnes) {
		try {
			this.nbColonnes = Integer.parseInt(aNbColonnes);
		} catch (e) {
			this.nbColonnes = -1;
		}
	},
	setNbLignes: function(aNbLignes) {
		try {
			this.nbLignes = Integer.parseInt(aNbLignes);
		} catch (e) {
			this.nbLignes = -1;
		}
	}
});