"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = require('syracuse-x3/lib/fusion/model/AdapiObservable').AdapiObservable;

var AdapiMenuAct = exports.AdapiMenuAct = helpers.defineClass(function(aclient, id, aEnabled) {
	AdapiObservable.call(this, aclient);
	this.id = id;
	this.enabled = aEnabled;
	// var dernier
	this.setJsonType('ACT');
}, AdapiObservable, {
	getObservableId: function() {
		return this.id.toString();
	},
	toJSON: function() {
		return {
			id: this.id.toString(),
			st: this.enabled === 1,
		};
	},
	toString: function() {
		return JSON.stringify(this.toJSON());
	}

});