"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = require('syracuse-x3/lib/fusion/model/AdapiObservable').AdapiObservable;

var AdapiTargetSpecial = exports.AdapiTargetSpecial = helpers.defineClass(function(name, client) {
	AdapiObservable.call(this, client);
	this.name = name;
	// En dernier
	this.setJsonType('TARGET');
}, AdapiObservable, {
	toJSON: function() {
		return {
			type: name
		};
	}
});