"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = require('syracuse-x3/lib/fusion/model/AdapiObservable').AdapiObservable;

var CAdapiException = exports.CAdapiException = helpers.defineClass(function(client, e, s, formatException) {
	Error.call(this, s + (e ? " [" + e.message + "]" : ""));
	AdapiObservable.call(this, client);
	console.log("!!! Error: " + e.stack);
	this.formatException = formatException;
	this.inner = e;
	this.diagnose = {
		$applicationCode: "ERROR",
		$code: "ApplicationDiagnosis",
		$severity: "ERROR",
		$stack: e && e.stack,
	}
	//this.setJsonType('DIAGNOSE');
}, Error, {
});

CAdapiException.newFmtException = function(client, e, s) {
	var r = new CAdapiException(client, e, s);
	r.formatException = true;
	return r;

}