"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CAdapiListOfEntity = require('syracuse-x3/lib/fusion/model/CAdapiListOfEntity').CAdapiListOfEntity;

var util = require('util');
var CAdapiListOfDataSrcs = exports.CAdapiListOfDataSrcs = helpers.defineClass(function(aRequesterWindow, aScreen) {
	CAdapiListOfEntity.call(this, aRequesterWindow);
	this.screen = aScreen;
}, CAdapiListOfEntity, {
	traverseAll: function(aTraverser) {
		var wMax = this.length;
		var wAdapiDataSrc;
		var wI = 0;
		while (wI < wMax) {
			wAdapiDataSrc = this.get(wI);
			if (aTraverser.doWhenTraverse(wAdapiDataSrc, wI) == true) {
				return true;
			}
			if (wAdapiDataSrc.traverseAll(aTraverser) == true) {
				return true;
			}
			wI++;
		}
		return false;
	},
	traverseChilds: function(aTraverser) {
		var wMax = this.length;
		var wEntry;
		var wI = 0;
		while (wI < wMax) {
			wEntry = this.get(wI);

			if (aTraverser.doWhenTraverse(wEntry, wI) == true) {
				return true;
			}
			wI++;
		}
		return false;
	},
	traverseSettable: function(aStructIst, aSetter) {
		var wI = aStructIst.blocZeroBase;
		if (wI < 0 || wI >= this.length) {
			var wMess = this.getClient().getMessage('ERROR_DSRC_UNKNOWN', wI.toString(), this.length.toString(), this.screen != null ? this.screen.getAdxId() : "???");
			aSetter.setLogicalErrMess(wMess);
			return false;
		}
		var wAdapiDataSrc = this.get(wI);
		return wAdapiDataSrc.traverseSettable(aStructIst, aSetter);
	}
});