"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CDefAction = require('syracuse-x3/lib/fusion/client/CDefAction').Actions;

var CAdapiPickedLine = exports.CAdapiPickedLine = helpers.defineClass(function(aStructIst, aAction) {
	this.ist = aStructIst;
	this.action = aAction;
}, null, {
	isSelection: function() {
		return this.action == CDefAction.SRV_TREESELECTLINE || this.action == CDefAction.SRV_BRWSELECTLINE;
	}
})