"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CAdapiVariableLocalMenu = require('syracuse-x3/lib/fusion/model/CAdapiVariableLocalMenu').CAdapiVariableLocalMenu;

var CAdapiVariableRadio = exports.CAdapiVariableRadio = helpers.defineClass(function(srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState, aLocalMenu) {
	CAdapiVariableLocalMenu.call(this, srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState, aLocalMenu);
}, CAdapiVariableLocalMenu, {
	clone: function() {
		var wVariable = new CAdapiVariableRadio(this.srcLine, this.entityIndexBaseOne, this.name, this.id, this.dataType, this.format, this.state, this.localMenu);
		wVariable.mustBeFormatted = this.mustBeFormatted;
		this.cloneValues(wVariable);
		return wVariable;
	},
	getDisplayValue: function() {
		if (this.srcLine.client.isInteractiveSession()) {
			if (this.serverValue != null) {
				return this.serverValue.value;
			} else {
				return CAdapiVariableLocalMenu.prototype.getDisplayValue.call(this);
			}
		} else {
			if (this.isStateEditable()) {
				return this.serverValue.value;
			} else {
				return CAdapiVariableLocalMenu.prototype.getDisplayValue.call(this);
			}
		}
	}
});