"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgAction = require('syracuse-x3/lib/fusion/client/CvgAction').Actions;

var CvgPickedLine = exports.CvgPickedLine = helpers.defineClass(function(aStructIst, aAction) {
	this.ist = aStructIst;
	this.action = aAction;
}, null, {
	isSelection: function() {
		return this.action == CvgAction.SRV_TREESELECTLINE || this.action == CvgAction.SRV_BRWSELECTLINE;
	}
})