"use strict"
require('npm-shadow')();

var path = require("path");
var config =  require('../../../../../nodelocal').config;
require('syracuse-core/lib/streamline-loader')(config.streamline);
require('etna-engine/lib/register'); // must be before streamline!

var file  = process.argv[2];
