"use strict";

var fs = require('fs');
var fsp = require('path');
var rules = require('tools/git/pre-commit-rules');

function doDir(path) {
	var stat = fs.statSync(path);
	if (stat.isDirectory()) {
		fs.readdirSync(path).forEach(function(s) {
			doDir(path + '/' + s);
		});
	} else if (stat.isFile()) {
		// console.log(path)
		rules.processFileSync(path, true);
	}
}

doDir(fsp.normalize(__dirname + "/../../../node_modules"));
rules.finish(true);