var fs = require('fs');

if (process.platform !== 'darwin') return; // enable later on windows.

var hooksDir = __dirname + '/../../../.git/hooks';

if (fs.existsSync(hooksDir)) {
	['commit-msg', 'pre-commit'].forEach(function(file) {
		fs.writeFileSync(hooksDir + "/" + file, "node node_modules/tools/git/" + file + " $1\n", "utf8");
		fs.chmodSync(hooksDir + "/" + file, "755");
	});
}