"use strict";
var X3Error = require("./runtime/errors").X3Error;

exports.print = function(str) {
	console.error(str);
};

exports.fail = function(message) {
	throw new Error(message);
};
exports.printMemory = function() {
	console.error(process.memoryUsage());
};
exports.printStack = function() {
	console.error(new X3Error(0, "stack dump").toString());
};