"use strict";
var errors = require('./errors');
var util = require('./util');
var date = require('syracuse-core/lib/types/date');
var time = require('syracuse-core/lib/types/time');
var datetime = require('syracuse-core/lib/types/datetime');
var ORIGIN = date.make(1600, 1, 1);

exports.functions = {
	DAY: {
		count: 1,
		fn: function(d) {
			return d.day;
		}
	},
	DAYN: {
		count: 1,
		fn: function(d) {
			// shift sunday from 0 to 7, others unchanged
			return 1 + (d.weekDay + 6) % 7;
		}
	},
	MONTH: {
		count: 1,
		fn: function(d) {
			return d.month;
		}
	},
	NDAY: {
		count: 1,
		fn: function(d) {
			return d.daysDiff(ORIGIN);
		}
	},
	WEEK: {
		count: 1,
		fn: function(d) {
			return d.week;
		}
	},
	YEAR: {
		count: 1,
		fn: function(d) {
			return d.year;
		}
	},
	DAY$: {
		count: 1,
		fn: function(d) {
			if (date.isDate(d)) d = d.weekDay;
			else if (util.isInteger(d)) d = d % 7;
			else throw util.badArgumentType(d);
			return date.dayName(d);
		}
	},
	MONTH$: {
		count: 1,
		fn: function(m) {
			if (date.isDate(m)) m = m.month;
			else if (util.isInteger(m)) m = 1 + (m + 11) % 12
			else throw util.badArgumentType(m);
			return date.monthName(m);
		}
	},
	TIME$: {
		count: 0,
		fn: function() {
			return time.now().toString();
		}
	},
	ADDMONTH: {
		count: 2,
		fn: function(d, dm) {
			return d.addMonths(dm);
		}
	},
	AWEEK: {
		count: 2,
		fn: function(w, y) {
			return date.makeInWeek(y, w, 1);
		}
	},
	DATE$: {
		count: 0,
		fn: function() {
			return date.today();
		}
	},
	EOMONTH: {
		count: 1,
		fn: function(d) {
			return d.endOfMonth();
		}
	},
	GDAT$: {
		count: 3,
		fn: function(d, m, y) {
			var dd = 0,
				dy = 0;
			if (d <= 0) dd = d - 1, d = 1;
			if (m <= 0 || m > 12) dy = Math.floor((m - 1) / 12), y += dy, m -= dy * 12;
			var d = date.make(y, m, d);
			return dd ? d.addDays(dd) : d;
		}
	},
	NDAY$: {
		count: 1,
		fn: function(n) {
			return ORIGIN.addDays(n);
		}
	},
	TIME: {
		count: 0,
		fn: function() {
			return time.now().value % (24 * 3600);
		}
	},
	TIMESTAMP$: {
		count: 0,
		fn: function() {
			return datetime.now().toString();
		}
	},

};

exports.instructions = {
	DATE: function(year, month, day) {
		return util.instructions.C(date.make(year, month, day));
	}
}